	},
];

export const GridList3 = () => {
	return (
		<div className='w-full h-full px-4 md:px-20 py-4'>
			<section className='w-full grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mt-30 pb-20'>
				{cards.map((card, index) => (
					<Card key={index} card={card} />
				))}
			</section>
		</div>
	);
};

export const Card = ({ card }: { card: (typeof cards)[0] }) => {
	return (
		<article className='w-full flex flex-col shadow rounded-lg border hover:border-primary/50 transition-colors'>
			<div className='w-full h-auto flex items-center gap-2 p-4'>
				<img
					src={card.avatar}
					alt='Avatar'
					className='rounded-full'
					width={40}
					height={40}
				/>
				<div className='flex flex-col items-start gap-1'>
					<div className='flex items-center gap-2'>
						<h2 className='text-md font-medium text-foreground'>{card.name}</h2>
					</div>
					<span className='text-sm text-muted-foreground'>{card.job}</span>
				</div>
			</div>
		</article>
	);
};
